import { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import api from "../api/api";
import { toast } from "react-toastify";
import { motion } from "framer-motion";

export default function Organizations() {
  const [orgs, setOrgs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAdd, setShowAdd] = useState(false);
  const [form, setForm] = useState({ name: "", description: "" });
  const [editingOrg, setEditingOrg] = useState(null);

  const fetchOrgs = async () => {
    try {
      setLoading(true);
      const res = await api.get("/orgs");
      setOrgs(res.data);
    } catch {
      toast.error("Failed to fetch organizations");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrgs();
  }, []);

  const addOrg = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/orgs", form);
      setOrgs(prev => [...prev, res.data]);
      toast.success("Organization added");
      setForm({ name: "", description: "" });
      setShowAdd(false);
    } catch {
      toast.error("Add failed");
    }
  };

  const updateOrg = async (e) => {
    e.preventDefault();
    try {
      const res = await api.put(`/orgs/${editingOrg.id}`, form);
      setOrgs(prev => prev.map(o => (o.id === editingOrg.id ? res.data : o)));
      toast.success("Organization updated");
      setEditingOrg(null);
      setForm({ name: "", description: "" });
      setShowAdd(false);
    } catch {
      toast.error("Update failed");
    }
  };

  const deleteOrg = async (id) => {
    if (!window.confirm("Delete this organization?")) return;
    try {
      await api.delete(`/orgs/${id}`);
      setOrgs(prev => prev.filter(o => o.id !== id));
      toast.success("Organization deleted");
    } catch {
      toast.error("Delete failed");
    }
  };

  const editOrg = (org) => {
    setEditingOrg(org);
    setForm({ name: org.name, description: org.description });
    setShowAdd(true);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <main className="flex-1 p-6">
        <Header title="Organizations" />
        <div className="mb-4 flex justify-between">
          <div />
          <button
            onClick={() => {
              setForm({ name: "", description: "" });
              setEditingOrg(null);
              setShowAdd(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Add Organization
          </button>
        </div>
        {loading ? (
          <div>Loading...</div>
        ) : orgs.length === 0 ? (
          <div>No organizations found.</div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {orgs.map((org) => (
              <motion.div
                key={org.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.1 }}
                className="bg-white rounded shadow p-4 flex flex-col"
              >
                <div className="text-lg font-semibold">{org.name}</div>
                <div className="text-sm text-gray-500 mb-4">{org.description}</div>
                <div className="flex gap-2 mt-auto">
                  <button
                    onClick={() => editOrg(org)}
                    className="px-3 py-1 bg-yellow-400 rounded hover:bg-yellow-500 transition"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteOrg(org.id)}
                    className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600 transition"
                  >
                    Delete
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        )}
        {showAdd && (
          <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
            <div className="bg-white rounded p-6 w-full max-w-md shadow-lg animate-fadeIn">
              <h4 className="font-semibold mb-3">
                {editingOrg ? "Edit Organization" : "Add Organization"}
              </h4>
              <form
                onSubmit={editingOrg ? updateOrg : addOrg}
                className="space-y-2"
              >
                <input
                  className="w-full border p-2 rounded"
                  placeholder="Name"
                  value={form.name}
                  onChange={e => setForm({ ...form, name: e.target.value })}
                  required
                />
                <input
                  className="w-full border p-2 rounded"
                  placeholder="Description"
                  value={form.description}
                  onChange={e =>
                    setForm({ ...form, description: e.target.value })
                  }
                />
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={() => setShowAdd(false)}
                    className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300 transition"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className={`px-3 py-1 rounded text-white ${
                      editingOrg
                        ? "bg-yellow-500 hover:bg-yellow-600"
                        : "bg-blue-600 hover:bg-blue-700"
                    } transition`}
                  >
                    {editingOrg ? "Update" : "Add"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
