import { useNavigate, useLocation } from "react-router-dom";
import { Home, Users, Building, LogOut } from "lucide-react";

export default function Sidebar() {
  const navigate = useNavigate();
  const location = useLocation();
  const items = [
    { label: "Dashboard", to: "/dashboard", icon: Home },
    { label: "Users", to: "/users", icon: Users },
    { label: "Organizations", to: "/orgs", icon: Building },
  ];

  return (
    <aside className="w-64 bg-white h-screen shadow-md sticky top-0 flex flex-col">
      <div className="p-4 border-b">
        <h1 className="text-xl font-semibold text-brand-500">OrgManage</h1>
        <p className="text-sm text-gray-500">SkillMatrix360</p>
      </div>
      <nav className="p-4 flex-1">
        <ul className="space-y-2">
          {items.map((it) => {
            const active = location.pathname === it.to;
            const Icon = it.icon;
            return (
              <li key={it.to}>
                <button
                  onClick={() => navigate(it.to)}
                  className={`w-full flex items-center gap-3 px-3 py-2 rounded-md transition ${
                    active ? "bg-blue-50 text-blue-600 shadow-sm" : "text-gray-700 hover:bg-gray-100"
                  }`}
                >
                  <Icon size={18} />
                  <span className="font-medium">{it.label}</span>
                </button>
              </li>
            );
          })}
        </ul>
      </nav>
      <div className="mt-auto p-4 border-t">
        <button
          onClick={() => {
            localStorage.removeItem("token");
            window.location.href = "/login";
          }}
          className="w-full flex items-center gap-2 px-3 py-2 rounded-md text-red-600 hover:bg-red-50"
        >
          <LogOut size={16} />
          Logout
        </button>
      </div>
    </aside>
  );
}
