import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import API from "../api/api";
import { motion } from "framer-motion";
import { FaUsers, FaBuilding, FaUserShield } from "react-icons/fa";

export default function Dashboard() {
  const [stats, setStats] = useState({
    users: 0,
    orgs: 0,
    admins: 0,
  });

  useEffect(() => {
    async function fetchStats() {
      try {
        const usersRes = await API.get("/users");
        const orgsRes = await API.get("/orgs");
        setStats({
          users: usersRes.data.length,
          orgs: orgsRes.data.length,
          admins: usersRes.data.filter(u => u.role === "admin").length,
        });
      } catch (err) {
        console.error("Failed to fetch stats", err);
      }
    }
    fetchStats();
  }, []);

  const cards = [
    {
      title: "Total Users",
      value: stats.users,
      icon: <FaUsers size={36} />,
      gradient: "bg-gradient-to-r from-blue-400 to-blue-600",
    },
    {
      title: "Organizations",
      value: stats.orgs,
      icon: <FaBuilding size={36} />,
      gradient: "bg-gradient-to-r from-green-400 to-green-600",
    },
    {
      title: "Active Admins",
      value: stats.admins,
      icon: <FaUserShield size={36} />,
      gradient: "bg-gradient-to-r from-purple-400 to-purple-600",
    },
  ];

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <main className="flex-1 p-6">
        <Header title="Dashboard" />
        <div className="mt-6 flex flex-col md:flex-row gap-6">
          {cards.map((card, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: idx * 0.2 }}
              className={`${card.gradient} text-white rounded-xl p-6 shadow-xl flex-1 flex items-center gap-4 hover:scale-105 transform transition-transform`}
            >
              <div>{card.icon}</div>
              <div className="flex flex-col">
                <span className="uppercase font-semibold text-sm">{card.title}</span>
                <span className="text-3xl font-bold">{card.value}</span>
              </div>
            </motion.div>
          ))}
        </div>
      </main>
    </div>
  );
}
