import { motion } from "framer-motion";

export default function Header({ title }) {
  const userJson = localStorage.getItem("user");
  const user = userJson ? JSON.parse(userJson) : null;

  return (
    <motion.header 
      initial={{ opacity: 0, y: -10 }}
      animate={{ opacity: 1, y: 0 }}
      className="flex items-center justify-between bg-white/80 backdrop-blur-sm p-6 rounded-2xl shadow-sm border border-gray-100 mb-6"
    >
      <div>
        <h1 className="text-2xl font-bold text-gray-800">{title}</h1>
        <p className="text-gray-500 text-sm mt-1">
          Manage your {title.toLowerCase()} efficiently
        </p>
      </div>
      
      {user && (
        <div className="flex items-center gap-4">
          <div className="text-right">
            <div className="font-semibold text-gray-800">{user.name}</div>
            <div className="text-xs text-gray-500 capitalize bg-gray-100 px-2 py-1 rounded-full">
              {user.role}
            </div>
          </div>
          <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-bold shadow-lg">
            {user.name.charAt(0).toUpperCase()}
          </div>
        </div>
      )}
    </motion.header>
  );
}
