import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import API from "../api/api";
import { motion, AnimatePresence } from "framer-motion";
import { 
  FaUsers, 
  FaBuilding, 
  FaUserShield, 
  FaChartLine,
  FaArrowUp,
  FaArrowDown
} from "react-icons/fa";

export default function Dashboard() {
  const [stats, setStats] = useState({
    users: 0,
    orgs: 0,
    admins: 0,
    growth: { users: 12, orgs: 8, admins: 5 }
  });

  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    async function fetchStats() {
      try {
        const usersRes = await API.get("/users");
        const orgsRes = await API.get("/orgs");
        setStats({
          users: usersRes.data.length,
          orgs: orgsRes.data.length,
          admins: usersRes.data.filter(u => u.role === "admin").length,
          growth: { users: 12, orgs: 8, admins: 5 }
        });
      } catch (err) {
        console.error("Failed to fetch stats", err);
      }
    }
    fetchStats();

    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const cards = [
    {
      title: "Total Users",
      value: stats.users,
      icon: <FaUsers className="filter drop-shadow-lg" size={32} />,
      gradient: "from-blue-500 to-cyan-500",
      border: "border-l-4 border-blue-400",
      growth: stats.growth.users,
      description: "Active users"
    },
    {
      title: "Organizations",
      value: stats.orgs,
      icon: <FaBuilding className="filter drop-shadow-lg" size={32} />,
      gradient: "from-emerald-500 to-green-500",
      border: "border-l-4 border-emerald-400",
      growth: stats.growth.orgs,
      description: "Registered organizations"
    },
    {
      title: "Active Admins",
      value: stats.admins,
      icon: <FaUserShield className="filter drop-shadow-lg" size={32} />,
      gradient: "from-purple-500 to-pink-500",
      border: "border-l-4 border-purple-400",
      growth: stats.growth.admins,
      description: "Administrative users"
    },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100
      }
    }
  };

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-gray-50 to-blue-50/30">
      <Sidebar />
      <main className="flex-1 p-6 lg:p-8">
        <Header title="Dashboard" />
        
        {/* Welcome Banner */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          className="mb-8 p-6 bg-gradient-to-r from-slate-800 to-slate-700 rounded-2xl text-white shadow-xl"
        >
          <div className="flex flex-col md:flex-row justify-between items-start md:items-center">
            <div>
              <h2 className="text-2xl font-bold mb-2">Welcome back! ðŸ‘‹</h2>
              <p className="text-blue-200">Here's what's happening with your organization today.</p>
            </div>
            <div className="mt-4 md:mt-0 text-sm bg-black/20 px-4 py-2 rounded-lg">
              {currentTime.toLocaleDateString('en-US', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric' 
              })}
            </div>
          </div>
        </motion.div>

        {/* Stats Cards */}
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8"
        >
          {cards.map((card, idx) => (
            <motion.div
              key={idx}
              variants={itemVariants}
              whileHover={{ 
                scale: 1.05,
                rotateY: 5,
                transition: { type: "spring", stiffness: 300 }
              }}
              whileTap={{ scale: 0.95 }}
              className={`bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 ${card.border} border-t border-r border-b border-white/20`}
            >
              <div className="flex items-center justify-between mb-4">
                <div className={`p-3 rounded-xl bg-gradient-to-r ${card.gradient} text-white shadow-lg`}>
                  {card.icon}
                </div>
                <div className="flex items-center space-x-1 bg-green-50 px-2 py-1 rounded-full">
                  <FaArrowUp className="text-green-500 text-xs" />
                  <span className="text-green-700 text-sm font-semibold">+{card.growth}%</span>
                </div>
              </div>
              
              <div className="space-y-2">
                <p className="text-gray-600 text-sm font-medium">{card.title}</p>
                <AnimatePresence mode="wait">
                  <motion.p
                    key={card.value}
                    initial={{ scale: 0.8, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    className="text-3xl font-bold bg-gradient-to-r bg-clip-text text-transparent from-gray-800 to-gray-600"
                  >
                    {card.value}
                  </motion.p>
                </AnimatePresence>
                <p className="text-gray-400 text-xs">{card.description}</p>
              </div>

              {/* Animated progress bar */}
              <div className="mt-4 w-full bg-gray-200 rounded-full h-2">
                <motion.div
                  initial={{ width: 0 }}
                  animate={{ width: `${Math.min(card.value * 2, 100)}%` }}
                  transition={{ delay: 0.5, duration: 1, ease: "easeOut" }}
                  className={`h-2 rounded-full bg-gradient-to-r ${card.gradient}`}
                />
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Additional Metrics Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.8 }}
          className="grid grid-cols-1 lg:grid-cols-2 gap-6"
        >
          {/* Quick Stats */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg">
            <div className="flex items-center space-x-2 mb-4">
              <FaChartLine className="text-blue-500" />
              <h3 className="font-semibold text-gray-800">Performance Overview</h3>
            </div>
            <div className="space-y-4">
              {[
                { label: "User Engagement", value: "87%", color: "bg-blue-500" },
                { label: "System Uptime", value: "99.9%", color: "bg-green-500" },
                { label: "Storage Used", value: "65%", color: "bg-amber-500" }
              ].map((metric, idx) => (
                <div key={idx} className="flex items-center justify-between">
                  <span className="text-gray-600">{metric.label}</span>
                  <div className="flex items-center space-x-2">
                    <div className="w-20 bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full ${metric.color} transition-all duration-1000 ease-out`}
                        style={{ width: metric.value }}
                      />
                    </div>
                    <span className="font-semibold text-gray-800">{metric.value}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Recent Activity */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg">
            <h3 className="font-semibold text-gray-800 mb-4">Recent Activity</h3>
            <div className="space-y-3">
              {[
                { action: "New user registration", time: "2 min ago", type: "user" },
                { action: "Organization created", time: "5 min ago", type: "org" },
                { action: "System backup completed", time: "1 hour ago", type: "system" }
              ].map((activity, idx) => (
                <motion.div
                  key={idx}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 1 + idx * 0.1 }}
                  className="flex items-center space-x-3 p-3 rounded-lg bg-gray-50/50 hover:bg-gray-100/50 transition-colors"
                >
                  <div className={`w-2 h-2 rounded-full ${
                    activity.type === 'user' ? 'bg-blue-500' : 
                    activity.type === 'org' ? 'bg-green-500' : 'bg-purple-500'
                  }`} />
                  <div className="flex-1">
                    <p className="text-sm font-medium text-gray-800">{activity.action}</p>
                    <p className="text-xs text-gray-500">{activity.time}</p>
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.div>
      </main>
    </div>
  );
}
