import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import API from "../api/api";
import { motion, AnimatePresence } from "framer-motion";
import { 
  FaUsers, 
  FaBuilding, 
  FaUserShield, 
  FaChartLine,
  FaArrowUp,
  FaActivity
} from "react-icons/fa";

export default function Dashboard() {
  const [stats, setStats] = useState({
    users: 0,
    orgs: 0,
    admins: 0,
    growth: { users: 12, orgs: 8, admins: 5 }
  });

  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    async function fetchStats() {
      try {
        const usersRes = await API.get("/users");
        const orgsRes = await API.get("/orgs");
        setStats({
          users: usersRes.data.length,
          orgs: orgsRes.data.length,
          admins: usersRes.data.filter(u => u.role === "admin").length,
          growth: { users: 12, orgs: 8, admins: 5 }
        });
      } catch (err) {
        console.error("Failed to fetch stats", err);
      }
    }
    fetchStats();

    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const cards = [
    {
      title: "Total Users",
      value: stats.users,
      icon: <FaUsers className="filter drop-shadow-lg" size={28} />,
      gradient: "from-blue-500 to-cyan-500",
      growth: stats.growth.users,
      description: "Active users in system",
      color: "blue"
    },
    {
      title: "Organizations",
      value: stats.orgs,
      icon: <FaBuilding className="filter drop-shadow-lg" size={28} />,
      gradient: "from-emerald-500 to-green-500",
      growth: stats.growth.orgs,
      description: "Registered organizations",
      color: "green"
    },
    {
      title: "Active Admins",
      value: stats.admins,
      icon: <FaUserShield className="filter drop-shadow-lg" size={28} />,
      gradient: "from-purple-500 to-pink-500",
      growth: stats.growth.admins,
      description: "Administrative users",
      color: "purple"
    },
  ];

  const recentActivities = [
    { action: "New user registration", time: "2 min ago", type: "user" },
    { action: "Organization created", time: "5 min ago", type: "org" },
    { action: "System backup completed", time: "1 hour ago", type: "system" },
    { action: "Profile updated", time: "2 hours ago", type: "user" }
  ];

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-blue-50/30 overflow-hidden">
      {/* Sidebar */}
      <Sidebar />
      
      {/* Main Content Area */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Main Content */}
        <div className="flex-1 overflow-y-auto">
          <div className="p-6 lg:p-8">
            <Header title="Dashboard" />
            
            {/* Welcome Banner */}
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              className="mb-8 bg-gradient-to-r from-blue-600 to-purple-700 rounded-2xl p-8 text-white shadow-xl"
            >
              <div className="flex flex-col lg:flex-row lg:items-center justify-between">
                <div className="mb-4 lg:mb-0">
                  <h1 className="text-3xl font-bold mb-3">Welcome back! ðŸ‘‹</h1>
                  <p className="text-blue-100 text-lg opacity-90">
                    Here's what's happening with your organization today.
                  </p>
                </div>
                <div className="bg-white/10 backdrop-blur-sm px-6 py-3 rounded-xl border border-white/20">
                  <div className="text-blue-100 font-semibold">
                    {currentTime.toLocaleDateString('en-US', { 
                      weekday: 'long', 
                      year: 'numeric', 
                      month: 'long', 
                      day: 'numeric' 
                    })}
                  </div>
                  <div className="text-blue-200 text-sm">
                    {currentTime.toLocaleTimeString()}
                  </div>
                </div>
              </div>
            </motion.div>

            {/* Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
              {cards.map((card, index) => (
                <motion.div
                  key={card.title}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  whileHover={{ y: -5, transition: { duration: 0.2 } }}
                  className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6 hover:shadow-xl transition-all duration-300"
                >
                  <div className="flex items-center justify-between mb-4">
                    <div className={`p-4 rounded-xl bg-gradient-to-r ${card.gradient} text-white shadow-lg`}>
                      {card.icon}
                    </div>
                    <div className="flex items-center space-x-1 bg-green-50 px-3 py-2 rounded-full">
                      <FaArrowUp className="text-green-500 text-xs" />
                      <span className="text-green-700 text-sm font-semibold">+{card.growth}%</span>
                    </div>
                  </div>
                  
                  <div className="space-y-2">
                    <p className="text-gray-600 text-sm font-medium">{card.title}</p>
                    <p className="text-3xl font-bold text-gray-800">{card.value}</p>
                    <p className="text-gray-400 text-xs">{card.description}</p>
                  </div>

                  {/* Progress Bar */}
                  <div className="mt-4 w-full bg-gray-200 rounded-full h-2">
                    <motion.div
                      initial={{ width: 0 }}
                      animate={{ width: `${Math.min(card.value * 5, 100)}%` }}
                      transition={{ delay: 0.5 + index * 0.1, duration: 1 }}
                      className={`h-2 rounded-full bg-gradient-to-r ${card.gradient}`}
                    />
                  </div>
                </motion.div>
              ))}
            </div>

            {/* Bottom Section */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Performance Metrics */}
              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.4 }}
                className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6"
              >
                <div className="flex items-center space-x-3 mb-6">
                  <div className="p-3 bg-blue-100 rounded-xl">
                    <FaChartLine className="text-blue-600 text-xl" />
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">Performance Metrics</h3>
                    <p className="text-gray-500 text-sm">System performance overview</p>
                  </div>
                </div>
                
                <div className="space-y-6">
                  {[
                    { label: "User Engagement", value: 87, color: "bg-blue-500" },
                    { label: "System Uptime", value: 99.9, color: "bg-green-500" },
                    { label: "Storage Usage", value: 65, color: "bg-amber-500" },
                    { label: "Task Completion", value: 78, color: "bg-purple-500" }
                  ].map((metric, idx) => (
                    <div key={idx} className="space-y-3">
                      <div className="flex justify-between items-center">
                        <span className="text-sm font-medium text-gray-700">{metric.label}</span>
                        <span className="text-sm font-semibold text-gray-800">{metric.value}%</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-3">
                        <motion.div
                          initial={{ width: 0 }}
                          animate={{ width: `${metric.value}%` }}
                          transition={{ delay: 0.6 + idx * 0.1, duration: 1 }}
                          className={`h-3 rounded-full ${metric.color} shadow-sm`}
                        />
                      </div>
                    </div>
                  ))}
                </div>
              </motion.div>

              {/* Recent Activity */}
              <motion.div
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.5 }}
                className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6"
              >
                <div className="flex items-center space-x-3 mb-6">
                  <div className="p-3 bg-purple-100 rounded-xl">
                    <FaActivity className="text-purple-600 text-xl" />
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">Recent Activity</h3>
                    <p className="text-gray-500 text-sm">Latest system activities</p>
                  </div>
                </div>

                <div className="space-y-4">
                  {recentActivities.map((activity, idx) => (
                    <motion.div
                      key={idx}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.6 + idx * 0.1 }}
                      className="flex items-center space-x-4 p-4 rounded-xl bg-gray-50 hover:bg-gray-100 transition-all duration-200 group cursor-pointer"
                    >
                      <div className={`w-3 h-3 rounded-full ${
                        activity.type === 'user' ? 'bg-blue-500' : 
                        activity.type === 'org' ? 'bg-green-500' : 'bg-purple-500'
                      }`} />
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-gray-800 group-hover:text-gray-900">
                          {activity.action}
                        </p>
                        <p className="text-xs text-gray-500">{activity.time}</p>
                      </div>
                      <div className="w-2 h-2 bg-gray-300 rounded-full group-hover:bg-gray-400" />
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
