import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import API from "../api/api";
import { toast } from "react-toastify";
import { motion } from "framer-motion";

export default function Users() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [form, setForm] = useState({ name: "", email: "", role: "" });

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const res = await API.get("/users");
      setUsers(res.data);
    } catch {
      toast.error("Failed to fetch users");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this user?")) return;
    try {
      await API.delete(`/users/${id}`);
      setUsers(users.filter(u => u.id !== id));
      toast.success("User deleted successfully");
    } catch {
      toast.error("Delete failed");
    }
  };

  const openEditModal = (user) => {
    setSelectedUser(user);
    setForm({ name: user.name, email: user.email, role: user.role });
    setShowEditModal(true);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await API.put(`/users/${selectedUser.id}`, form);
      setUsers(users.map(u => (u.id === selectedUser.id ? res.data.user : u)));
      toast.success("User updated successfully");
      setShowEditModal(false);
    } catch {
      toast.error("Update failed");
    }
  };

  return (
    <div className="flex min-h-screen">
      <Sidebar />
      <main className="flex-1 p-6 bg-gray-100">
        <Header title="Users" />

        <motion.div className="overflow-x-auto mt-6" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <table className="min-w-full bg-white rounded shadow-lg">
            <thead>
              <tr className="bg-gray-200 text-left">
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Email</th>
                <th className="px-4 py-2">Role</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr><td colSpan="4" className="text-center py-4">Loading...</td></tr>
              ) : users.map(user => (
                <tr key={user.id} className="hover:bg-gray-50 transition-colors">
                  <td className="px-4 py-2">{user.name}</td>
                  <td className="px-4 py-2">{user.email}</td>
                  <td className="px-4 py-2">
                    <span className={`px-2 py-1 rounded-full text-white text-sm ${user.role === "admin" ? "bg-purple-500" : "bg-blue-500"}`}>
                      {user.role}
                    </span>
                  </td>
                  <td className="px-4 py-2 flex gap-2">
                    <button onClick={() => openEditModal(user)} className="px-3 py-1 bg-yellow-400 rounded">Edit</button>
                    <button onClick={() => handleDelete(user.id)} className="px-3 py-1 bg-red-500 text-white rounded">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </motion.div>

        {showEditModal && (
          <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
            <div className="bg-white rounded p-6 w-full max-w-md">
              <h3 className="text-lg font-semibold mb-4">Edit User</h3>
              <form onSubmit={handleUpdate} className="space-y-3">
                <input
                  className="w-full border p-2 rounded"
                  value={form.name}
                  onChange={e => setForm({ ...form, name: e.target.value })}
                  placeholder="Name"
                  required
                />
                <input
                  className="w-full border p-2 rounded"
                  value={form.email}
                  onChange={e => setForm({ ...form, email: e.target.value })}
                  placeholder="Email"
                  required
                />
                <select
                  className="w-full border p-2 rounded"
                  value={form.role}
                  onChange={e => setForm({ ...form, role: e.target.value })}
                >
                  <option value="user">User</option>
                  <option value="admin">Admin</option>
                </select>
                <div className="flex justify-end gap-2">
                  <button type="button" onClick={() => setShowEditModal(false)} className="px-3 py-1 bg-gray-200 rounded">Cancel</button>
                  <button type="submit" className="px-3 py-1 bg-green-500 text-white rounded">Save</button>
                </div>
              </form>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
