import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import API from "../api/api";

export default function Dashboard() {
  const [stats, setStats] = useState({
    users: 0,
    orgs: 0,
    admins: 0,
  });

  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    async function fetchStats() {
      try {
        const usersRes = await API.get("/users");
        const orgsRes = await API.get("/orgs");
        setStats({
          users: usersRes.data.length,
          orgs: orgsRes.data.length,
          admins: usersRes.data.filter(u => u.role === "admin").length,
        });
      } catch (err) {
        console.error("Failed to fetch stats", err);
      }
    }
    fetchStats();

    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const cards = [
    {
      title: "Total Users",
      value: stats.users,
      growth: "+12%",
      description: "Active users in system",
      color: "blue"
    },
    {
      title: "Organizations",
      value: stats.orgs,
      growth: "+8%",
      description: "Registered organizations",
      color: "green"
    },
    {
      title: "Active Admins",
      value: stats.admins,
      growth: "+5%",
      description: "Administrative users",
      color: "purple"
    },
  ];

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#f9fafb' }}>
      <Sidebar />
      
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <Header title="Dashboard" />
        
        <main style={{ flex: 1, padding: '1.5rem', overflowY: 'auto' }}>
          {/* Welcome Banner */}
          <div style={{
            background: 'linear-gradient(to right, #3b82f6, #8b5cf6)',
            borderRadius: '1rem',
            padding: '2rem',
            color: 'white',
            marginBottom: '2rem',
            boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', justifyContent: 'space-between', gap: '1rem' }}>
              <div>
                <h1 style={{ fontSize: '1.875rem', fontWeight: 'bold', marginBottom: '0.75rem' }}>Welcome back! 👋</h1>
                <p style={{ color: '#dbeafe', fontSize: '1.125rem' }}>
                  Here's what's happening with your organization today.
                </p>
              </div>
              <div style={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.1)', 
                backdropFilter: 'blur(4px)',
                padding: '0.75rem 1.5rem',
                borderRadius: '0.75rem',
                border: '1px solid rgba(255, 255, 255, 0.2)'
              }}>
                <div style={{ color: '#dbeafe', fontWeight: '600', fontSize: '1.125rem' }}>
                  {currentTime.toLocaleDateString('en-US', { 
                    weekday: 'long', 
                    year: 'numeric', 
                    month: 'long', 
                    day: 'numeric' 
                  })}
                </div>
                <div style={{ color: '#bfdbfe', fontSize: '0.875rem', fontFamily: 'monospace' }}>
                  {currentTime.toLocaleTimeString()}
                </div>
              </div>
            </div>
          </div>

          {/* Stats Grid */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
            gap: '1.5rem',
            marginBottom: '2rem'
          }}>
            {cards.map((card, index) => (
              <div key={card.title} style={{
                backgroundColor: 'white',
                borderRadius: '1rem',
                padding: '1.5rem',
                boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
                border: '1px solid #f3f4f6'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1rem' }}>
                  <div style={{
                    padding: '1rem',
                    borderRadius: '0.75rem',
                    background: card.color === 'blue' ? 'linear-gradient(to right, #3b82f6, #06b6d4)' :
                              card.color === 'green' ? 'linear-gradient(to right, #10b981, #22c55e)' :
                              'linear-gradient(to right, #a855f7, #ec4899)',
                    color: 'white',
                    boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
                  }}>
                    {/* Icon placeholder */}
                    <div style={{ width: '28px', height: '28px', backgroundColor: 'rgba(255,255,255,0.3)', borderRadius: '6px' }}></div>
                  </div>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '0.25rem', 
                    backgroundColor: '#dcfce7', 
                    padding: '0.5rem 0.75rem',
                    borderRadius: '9999px'
                  }}>
                    <span style={{ color: '#16a34a', fontSize: '0.75rem' }}>▲</span>
                    <span style={{ color: '#166534', fontSize: '0.875rem', fontWeight: '600' }}>{card.growth}</span>
                  </div>
                </div>
                
                <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                  <p style={{ color: '#4b5563', fontSize: '0.875rem', fontWeight: '500' }}>{card.title}</p>
                  <p style={{ fontSize: '1.875rem', fontWeight: 'bold', color: '#1f2937' }}>{card.value}</p>
                  <p style={{ color: '#9ca3af', fontSize: '0.75rem' }}>{card.description}</p>
                </div>

                {/* Progress Bar */}
                <div style={{ marginTop: '1rem', width: '100%', backgroundColor: '#e5e7eb', borderRadius: '9999px', height: '0.5rem' }}>
                  <div style={{
                    height: '0.5rem',
                    borderRadius: '9999px',
                    background: card.color === 'blue' ? 'linear-gradient(to right, #3b82f6, #06b6d4)' :
                              card.color === 'green' ? 'linear-gradient(to right, #10b981, #22c55e)' :
                              'linear-gradient(to right, #a855f7, #ec4899)',
                    width: `${Math.min(card.value * 5, 100)}%`
                  }}></div>
                </div>
              </div>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
}
